mapper.select:
select
	a.id,a.artName,a.artNumber,a.artInnerNumber,a.artImage,a.artAudio,a.artYear,a.artAuthor,a.artSize,a.artMaterial,a.artCert,a.artDesc
from 

	art a ,art_museum m


	art a



		a.id = m.artId and m.museumId = #{batchId}


		and a.artName like #{name}


		and a.artNumber like #{number}


		and a.artInnerNumber like #{innerNumber}


		and a.id = #{id}


		and a.status = #{status}


		and a.createTime > #{beginDate}


		and #{endDate} > a.createTime


	limit #{start}, #{limit}

select
	count(a.id)
from 

	art a ,art_museum m


	art a



		a.id = m.artId and m.museumId = #{batchId}


		and a.artName like #{name}


		and a.artNumber like #{number}


		and a.artInnerNumber like #{innerNumber}


		and a.id = #{id}


		and a.status = #{status}


		and a.createTime > #{beginDate}


		and #{endDate} > a.createTime

mapper.update:
update 
	art
set 
	artImage = #{artImage}
where 
	id = #{id}

update 
	art
set 
	artAudio = #{artAudio}
where 
	id = #{id}

update museum 
set id = #{id}

	,name = #{name}


	,location = #{location}

where id = #{id}

mapper.delete:
delete from 
	art_museum


		and artId = ${artId}


		and museumId = ${museumId}
